const INPUT: &'static [u8] = "".as_bytes();

fn condition(i: usize, num: usize) -> bool {
    for (j, elem1) in INPUT[i..i + num].iter().enumerate() {
        for elem2 in INPUT[j + i + 1..i + num + 1].iter() {
            if elem1 == elem2 {
                return false;
            }
        }
    }
    return true;
}

fn solve(size: usize) -> usize {
    for i in 0..INPUT.len() - size {
        if condition(i, size - 1) {
            return i + size;
        }
    }
    unreachable!();
}

pub fn run() {
    println!("day06");
    println!("PART 1: {}", solve(4));
    println!("PART 2: {}", solve(14));
}
